#!/usr/bin/env sh

set -xeuo

### Script to setup the following:
# - An L1 based on geth and lighthouse
# - The AggLayer and AggLayer prover
# - The L1 smart contracts for the bridge and rollupmanager
# - An L2 running cdk-erigon
# - Another separate L2 running op geth
#############################################

### Requirements:
# - Docker
# - Kurtosis CLI
# - Cast
# - jq
# - git
#############################################

# Deploy the network
kurtosis run --enclave cdk github.com/0xPolygon/kurtosis-cdk@c97d876e323a2ba85d056744ffb0fb656df6993c '{"args":{"zkevm_contracts_image":"leovct/zkevm-contracts:v9.0.0-rc.3-pp-fork.12-patch.1","additional_services":[],"consensus_contract_type":"pessimistic","sequencer_type":"erigon","erigon_strict_mode":false,"gas_token_enabled":false,"zkevm_use_real_verifier":true,"enable_normalcy":true}, "deployment_stages": {"deploy_optimism_rollup": true }}'

# Sovereign rollup sequencer keypair
sov_admin_addr=0x8281AdB2fC133536ACDC4c923bc573A26f66F260
sov_admin_private_key=0x45f3ccdaff88ab1b3bb41472f09d5cde7cb20a6cbbc9197fddf64e2f3d67aaf2

rollupTypeID=1
chainID=8888
admin=$sov_admin_addr
sequencer=$sov_admin_addr
gasTokenAddress=$(cast az)
sequencerURL=http://op-el-1-op-geth-op-node-op-kurtosis:8545
networkName="Sovereign Chain"
rollup_manager_addr="0x2F50ef6b8e8Ee4E579B17619A92dE3E2ffbD8AD2"
rollup_admin_key="0x12d7de8621a77640c9241b2595ba78ce443d05e94090365ab3bb5e19df82c625"

# Create sovereign network rollup
cast send --private-key $rollup_admin_key --rpc-url http://$(kurtosis port print cdk el-1-geth-lighthouse rpc) $rollup_manager_addr 'createNewRollup(uint32,uint64,address,address,address,string,string)' $rollupTypeID $chainID $admin $sequencer $gasTokenAddress $sequencerURL $networkName

# Display the rollup information
cast call --json --rpc-url http://$(kurtosis port print cdk el-1-geth-lighthouse rpc) 0x2F50ef6b8e8Ee4E579B17619A92dE3E2ffbD8AD2 'rollupIDToRollupData(uint32)(address,uint64,address,uint64,bytes32,uint64,uint64,uint64,uint64,uint64,uint64,uint8)' 2 | jq '{"rollupContract": .[0], "chainID": .[1], "verifier": .[2], "forkID": .[3], "lastLocalExitRoot": .[4], "lastBatchSequenced": .[5], "lastVerifiedBatch": .[6], "_legacyLastPendingState": .[7], "_legacyLastPendingStateConsolidated": .[8], "lastVerifiedBatchBeforeUpgrade": .[9], "rollupTypeID": .[10], "rollupVerifierType": .[11]}'

bridge_admin_addr=0x72aA7C55e1c7BF4017F22a3bc19722de11911A81
bridge_admin_private_key=0x5f3556010771f2cc34eb2669401ee1109bc05aed993024ed10ff04ba7309e28b
aggoracle_addr=0x2Ac2c49Ee3Ac5f663115C86F405Ea855B365D5Ec
aggoracle_private_key=0xd65de4634c214d45673528bf55be28fe43b0664c99cc99089ef75a922b3a22fd
claimtx_addr=0x3754Aa77EE1E8AfB200Ce36a8c943ed8F5AaB7BC
claimtx_private_key=0xfa333c42db7bc56277bf67c93ba19e4f414d802ef9886b8b5dc7c450655ae77f
rpc_url=$(kurtosis port print cdk op-el-1-op-geth-op-node-op-kurtosis rpc)
private_key=$(cast wallet private-key --mnemonic 'test test test test test test test test test test test junk') # This is the default prefunded account for the OP Network

# Fund necessary accounts on sovereign chain
# These are some accounts that we want to fund for operations for running claims
cast send --value 10ether --rpc-url $rpc_url --private-key $private_key $bridge_admin_addr
cast send --value 10ether --rpc-url $rpc_url --private-key $private_key $aggoracle_addr
cast send --value 100ether --rpc-url $rpc_url --private-key $private_key $claimtx_addr

# Deploy the bridge contracts
sovereign_bridge_
global_exit_root_manager_
bridge_impl_nonce=$(cast nonce --rpc-url $rpc_url $bridge_admin_addr)
bridge_impl_addr=$(cast compute-address --nonce $bridge_impl_nonce $bridge_admin_addr | sed 's/.*: //')
ger_impl_addr=$(cast compute-address --nonce $((bridge_impl_nonce + 1)) $bridge_admin_addr | sed 's/.*: //')
ger_proxy_addr=$(cast compute-address --nonce $((bridge_impl_nonce + 2)) $bridge_admin_addr | sed 's/.*: //')
bridge_proxy_addr=$(cast compute-address --nonce $((bridge_impl_nonce + 3)) $bridge_admin_addr | sed 's/.*: //')
ger_constructor_args=$(cast abi-encode "constructor(address)" $bridge_proxy_addr)

# Deploy the bridge implementation
cast send --value 1000ether --rpc-url $rpc_url --private-key $private_key $bridge_proxy_addr
cast send --private-key $bridge_admin_private_key --rpc-url $rpc_url --create $sovereign_bridge_bytecode

# Deploy the GER implementation
cast send --private-key $bridge_admin_private_key --rpc-url $rpc_url --create $(cast concat-hex $global_exit_root_manager_bytecode $ger_constructor_args)

transparent_upgradeable_proxy_
ger_proxy_calldata=$(cast calldata 'initialize(address _globalExitRootUpdater, address _globalExitRootRemover)' $aggoracle_addr $aggoracle_addr)
ger_proxy_constructor_args=$(cast abi-encode "constructor(address,address,bytes)" $ger_impl_addr $bridge_admin_addr $ger_proxy_calldata)

# Deploy the GER proxy
cast send --private-key $bridge_admin_private_key --rpc-url $rpc_url --create $(cast concat-hex $transparent_upgradeable_proxy_bytecode $ger_proxy_constructor_args)

initNetworkID=2
initGasTokenAddress=$(cast az)
initGasTokenNetwork=0
initGlobalExitRootManager=$ger_proxy_addr
initPolygonRollupManager=$(cast az)
initGasTokenMetadata=0x
initBridgeManager=$bridge_admin_addr
initSovereignWETHAddress=$(cast az)
initSovereignWETHAddressIsNotMintable=false
bridge_proxy_calldata=$(cast calldata 'initialize(uint32 _networkID, address _gasTokenAddress, uint32 _gasTokenNetwork, address _globalExitRootManager, address _polygonRollupManager, bytes _gasTokenMetadata, address _bridgeManager, address _sovereignWETHAddress, bool _sovereignWETHAddressIsNotMintable)' $initNetworkID $initGasTokenAddress $initGasTokenNetwork $initGlobalExitRootManager $initPolygonRollupManager $initGasTokenMetadata $initBridgeManager $initSovereignWETHAddress $initSovereignWETHAddressIsNotMintable)
bridge_proxy_constructor_args=$(cast abi-encode "constructor(address,address,bytes)" $bridge_impl_addr $bridge_admin_addr $bridge_proxy_calldata)

# Deploy the bridge proxy
cast send --private-key $bridge_admin_private_key --rpc-url $rpc_url --gas-limit 5000000 --create $(cast concat-hex $transparent_upgradeable_proxy_bytecode $bridge_proxy_constructor_args)

# Display the bridge information
cast balance --ether --rpc-url $rpc_url $bridge_proxy_addr
cast code --rpc-url $rpc_url $bridge_proxy_addr

# Make accounts for the bridge service and AggKit
cast wallet import --private-key 0xd65de4634c214d45673528bf55be28fe43b0664c99cc99089ef75a922b3a22fd --keystore-dir conf --unsafe-password optest aggoracle
cast wallet import --private-key 0x45f3ccdaff88ab1b3bb41472f09d5cde7cb20a6cbbc9197fddf64e2f3d67aaf2 --keystore-dir conf --unsafe-password optest aggsender
cast wallet import --private-key 0xfa333c42db7bc56277bf67c93ba19e4f414d802ef9886b8b5dc7c450655ae77f --keystore-dir conf --unsafe-password optest claimtx

# Check if the docker images with tag "cdk:local" exists
if [ $(docker images -q cdk:local | wc -c) -ne 0 ]; then
    echo "Docker image with tag 'cdk:local' already exists"
else
    echo "Building Docker image with tag 'cdk:local'"
    git clone https://github.com/0xPolygon/cdk.git -b develop
    cd cdk
    git reset --hard ff9ce60c8df998c8a06e215a0c3c498e6dfa67f5
    make build-docker
    docker image tag cdk cdk:local
    cd ../
    rm -rf cdk # cleanup
fi

#Make the cdk.toml file
echo "PathRWData = \"/tmp/\"
L1URL=\"http://el-1-geth-lighthouse:8545\"
L2URL=\"http://op-el-1-op-geth-op-node-op-kurtosis:8545\"
AggLayerURL=\"http://agglayer:4444\"

ForkId = 12
ContractVersions = \"banana\"
IsValidiumMode = false

# This is the admin account for now
L2Coinbase =  \"0x8281AdB2fC133536ACDC4c923bc573A26f66F260\"
SequencerPrivateKeyPath = \"\"
SequencerPrivateKeyPassword  = \"\"

AggregatorPrivateKeyPath = \"\"
AggregatorPrivateKeyPassword  = \"\"
SenderProofToL1Addr = \"\"
polygonBridgeAddr = \"0xd8886e9D827218a02B8C04323b5550f2F36BC8d5\"

RPCURL = \"http://op-el-1-op-geth-op-node-op-kurtosis:8545\"
WitnessURL = \"\"

# This values can be overriden directly from genesis.json
rollupCreationBlockNumber = \"39\"
rollupManagerCreationBlockNumber = \"39\"
genesisBlockNumber = \"39\"

[L1Config]
chainId = \"271828\"
polygonZkEVMGlobalExitRootAddress = \"0x1f7ad7caA53e35b4f0D138dC5CBF91aC108a2674\"
polygonRollupManagerAddress = \"0x2F50ef6b8e8Ee4E579B17619A92dE3E2ffbD8AD2\"
polTokenAddress = \"0xEdE9cf798E0fE25D35469493f43E88FeA4a5da0E\"
# This aligns with the address that we created
polygonZkEVMAddress = \"0xcC626369bD1ff281b22B2dfA71ce0B4776A16568\"

[L2Config]
GlobalExitRootAddr = \"0x38b6Ba96AC4149888e27AA4315DDC366f1676BCf\"

[Log]
Environment = \"development\" # "production" or "development"
Level = \"info\"
Outputs = [\"stderr\"]

[RPC]
Port = 5576

[AggSender]
AggsenderPrivateKey = {Path = \"/etc/cdk/aggsender\", Password = \"optest\"}

[AggOracle.EVMSender]
GlobalExitRootL2=\"0x38b6Ba96AC4149888e27AA4315DDC366f1676BCf\"

[AggOracle.EVMSender.EthTxManager]
PrivateKeys = [{Path = \"/etc/cdk/aggoracle\", Password = \"optest\"}]

[AggOracle.EVMSender.EthTxManager.Etherman]
# Ok this is bizarre, but needs to be set to be the L2 chain id
L1ChainID = 2151908

[BridgeL2Sync]
BridgeAddr = \"0x9A1f8eA578835d2b7b1e1EB8CD5EE3Bb7692338C\"

[L1InfoTreeSync]
# The initial block number from which to start syncing.
# Default: 0
InitialBlock = \"39\"" >conf/cdk.toml

# Start the AggKit service
docker run -d --name aggoraclesender --rm --network kt-cdk -v $PWD/conf:/etc/cdk cdk:local cdk-node run --cfg /etc/cdk/cdk.toml --components aggsender,aggoracle

# Start the bridge service database
docker run --network kt-cdk -d --rm --name bridge-service-db -e POSTGRES_PASSWORD=optest -e POSTGRES_DB=bridge_db -e POSTGRES_USER=bridge_user postgres:17.2

# Make the bridge service configuration file
echo "[Log]
Level = \"info\"
Environment = \"production\"
Outputs = [\"stderr\"]

[SyncDB]
Database = \"postgres\"
User = \"bridge_user\"
Name = \"bridge_db\"
Password = \"optest\"
Host = \"bridge-service-db\"
Port = \"5432\"
MaxConns = 20

[Etherman]
l1URL = \"http://el-1-geth-lighthouse:8545\"
L2URLs = [\"http://op-el-1-op-geth-op-node-op-kurtosis:8545\"]

[Synchronizer]
SyncInterval = \"5s\"
SyncChunkSize = 100

[BridgeController]
Store = \"postgres\"
Height = 32

[BridgeServer]
GRPCPort = \"9090\"
HTTPPort = \"8080\"
DefaultPageLimit = 25
MaxPageLimit = 1000
BridgeVersion = \"v1\"
# Read only
[BridgeServer.DB]
Database = \"postgres\"
User = \"bridge_user\"
Name = \"bridge_db\"
Password = \"optest\"
Host = \"bridge-service-db\"
Port = \"5432\"
MaxConns = 20

[NetworkConfig]
GenBlockNumber = \"39\"
PolygonBridgeAddress = \"0xd8886e9D827218a02B8C04323b5550f2F36BC8d5\"
PolygonZkEVMGlobalExitRootAddress = \"0x1f7ad7caA53e35b4f0D138dC5CBF91aC108a2674\"
PolygonRollupManagerAddress = \"0x2F50ef6b8e8Ee4E579B17619A92dE3E2ffbD8AD2\"
PolygonZkEVMAddress = \"0xcC626369bD1ff281b22B2dfA71ce0B4776A16568\"
L2PolygonBridgeAddresses = [\"0x9A1f8eA578835d2b7b1e1EB8CD5EE3Bb7692338C\"]

SovereignChains = [true]
L2PolygonZkEVMGlobalExitRootAddresses = [\"0x38b6Ba96AC4149888e27AA4315DDC366f1676BCf\"]

[ClaimTxManager]
FrequencyToMonitorTxs = \"5s\"
PrivateKey = {Path = \"/etc/bridge/claimtx\", Password = \"optest\"}
Enabled = true
RetryInterval = \"1s\"
RetryNumber = 10" >conf/bridge.toml

# Start the bridge service
docker run -d -p 3080:8080 -v $PWD/conf:/etc/bridge --network kt-cdk --rm --name bridge-service hermeznetwork/zkevm-bridge-service:sovereign-test /app/zkevm-bridge run --cfg /etc/bridge/bridge.toml

# Put the network rpc url in the .env file
echo "LAYER_X_RPC_URL=http://$(kurtosis port print cdk el-1-geth-lighthouse rpc)" >>.env
echo "LAYER_Y_RPC_URL=$(kurtosis port print cdk op-el-1-op-geth-op-node-op-kurtosis rpc)" >>.env
